function jivo_onLoadCallback() {

    var contact = jivo_api.getContactInfo();
    var user = false;
    var date = new Date();
    var time = date.getHours();
    var wellcome_message;

    var user_name;

    if (contact.name) {
        user_name = contact.name;
    } else {
        user_name = user.NAME;
    }

    wellcome_message = "Здравствуйте";

    if (user_name) {
        wellcome_message += ", " + user_name;
    }

    wellcome_message += "! ";

    if ((time > 8) && (time < 20)) {
        if (typeof(product) == "object") {
            wellcome_message += "Вас заинтересовала ";

            if (product.IBLOCK_CODE != "arch") {
                wellcome_message += "модель ";
            }

            wellcome_message += product.NAME + "? ";
            wellcome_message += "Я могу предоставить Вам более полную информацию по ней.";

        } else if (typeof(basket) == "object") {
            if (basket.FULL === true) {
                wellcome_message += "Вы на предпоследнем шаге от оформления заказа. Возможно у вас появились вопросы?";
            }
        } else if (typeof(category) == "object") {
            if ((category.IBLOCK_CODE == "dveri")) {
                if (category.SORT == "sale") {
                    wellcome_message += "Я могу Вам помочь подобрать наиболее выгодный вариант из распродажи";
                } else {
                    wellcome_message += "Я могу Вам помочь подобрать двери с учетом Ваших требований и вкуса. Задавайте вопросы";
                }
            } else if (category.IBLOCK_CODE == "arch") {
                wellcome_message += "Я могу Вам помочь подобрать арки с учетом Ваших требований и вкуса. Задавайте вопросы";
            } else if (category.IBLOCK_CODE == "furniture") {

                if (category.SECTION_CODE == "handles") {
                    wellcome_message += "Я могу Вам помочь подобрать дверный ручки с учетом Ваших требований и вкуса. Задавайте вопросы";
                } else if (category.SECTION_CODE == "ogranichiteli") {
                    wellcome_message += "Я могу Вам помочь подобрать ограничители с учетом Ваших требований и вкуса. Задавайте вопросы";
                } else if (category.SECTION_CODE == "petli") {
                    wellcome_message += "Я могу Вам помочь подобрать петли с учетом Ваших требований и вкуса. Задавайте вопросы";
                } else if (category.SECTION_CODE == "razdvizhnaya-sistema") {
                    wellcome_message += "Я могу Вам помочь подобрать раздвижную систему с учетом Ваших требований и вкуса. Задавайте вопросы";
                } else if (category.SECTION_CODE == "shpingalety") {
                    wellcome_message += "Я могу Вам помочь подобрать шпингалеты с учетом Ваших требований и вкуса. Задавайте вопросы";
                } else {
                    wellcome_message += "Я могу Вам помочь подобрать фурнитуру с учетом Ваших требований и вкуса. Задавайте вопросы";
                }

            }

        } else if (typeof(buyers) == "object") {
            switch (buyers.page) {
                case 'aktsii' :
                    wellcome_message += "Я могу Вам помочь в подборе акционных товаров, с учетом Ваших пожеланий";
                    break;
                case 'kak-kupit' :
                    wellcome_message += "Я могу проконсультировать Вас, как пошагово купить у нас двери";
                    break;
                case 'uslugi' :
                    wellcome_message += "Я могу предоставить детальную информацию по нашим услугам";
                    break;
                case 'dostavka' :
                    wellcome_message += "Я могу предоставить детальную информацию по доставке и оплате";
                    break;
                case 'garantii' :
                    wellcome_message += "Я могу предоставить детальную информацию по гарантии товара и установки";
                    break;
            }
        } else if (typeof(partners) == "object") {
            if (partners.page === true) {
                wellcome_message += "Хотите стать нашим партнером? Наверняка у Вас есть вопросы, я могу Вас проконсультировать";
            }
        } else if (typeof(saloni) == "object") {
            if (saloni.page === true) {
                wellcome_message += "Вы находитесь на карте со всеми нашими магазинами. Я могу Вам помочь, найти подходящий для Вас магазин";
            }
        } else {
            wellcome_message += "Рады видеть Вас на нашем сайте и предоставить полную консультацию";
        }
    } else {
        wellcome_message += "Мы сможем Вам помочь подобрать двери с учетом Ваших требований и вкуса. Ваша заявка будет обработана в период с 09.00 по 20.00 по Мск";
    }

    jivo_config.rules = [
        {
            "name": "Активное приглашение в диалог",
            "enabled": true,
            "type": "all",
            "conditions": [
                {
                    "condition": "online",
                    "value": true
                },
                {
                    "condition": "time_on_page",
                    "comparator": "greater",
                    "value": 20
                },
                {
                    "condition": "time_on_site",
                    "comparator": "greater",
                    "value": 60
                }
            ],
            "commands": [
                {
                    "command": "proactive",
                    "params": {
                        "message": wellcome_message
                    }
                }
            ]
        }
    ];
}
